	/*** Create new invoice form start ***/

	public function index() {
		$api_response = $this->getIndexApi();
		$api_response_data = $api_response->getData();
		if($api_response_data->status == 'success')
		{
			$invoicelists	= $api_response_data->data->invoicelist;
			return view('admin/invoice/list',compact('invoicelists'));	
		}
	}

	public function create()
	{
		$api_response = $this->getCreateApi();
		$api_response_data = $api_response->getData();
		if($api_response_data->status == 'success')
		{
			$practicelist	= $api_response_data->data->practicelist;
			return view('admin/invoice/create',compact('practicelist'));	
		}
	}
	/*** Create new invoice form end ***/
	
	/*** Invoice details start ***/
	public function generateInvoice(Request $request)
	{
		$api_response = $this->postInvoiceApi($request::all());
		$api_response_data = $api_response->getData();
		if($api_response_data->status == 'success')
		{
			$practice = $api_response_data->data->practice;
			// dd($practice);
			$invoice = $api_response_data->data->invoice;
			$message = $api_response_data->message;
			if(count($invoice)>0){ $invoice_no = ++$invoice->invoice_no;}
			else{ $invoice_no = "MC100001"; }

			return view('admin/invoice/invoice',compact('invoice_no','practice','message'));
		}
		else
		{
			return Redirect::to('admin/invoice')->withInput()->withErrors($api_response_data->message);
		}    
	}
	/*** Invoice details end ***/

		/*** Store invoice details start ***/
	public function store(Request $request)
	{
		$api_response = $this->storeInvoiceApi($request::all());
		$api_response_data = $api_response->getData();
		if($api_response_data->status == 'success')
		{
			$message = $api_response_data->message;
			return Redirect::to('admin/invoice')->withInput();
		}
		else
		{
			return Redirect::to('admin/invoice')->withInput()->withErrors($api_response_data->message);
		}    
	}
